@MainActor func transient_Preferences_mValueRevealInFinder_fonts (
       _ self_usesUserLibrary : Bool,                             
       _ self_additionnalLibraryArray_mUses : [CanariLibraryEntry_mUses],
       _ self_additionnalLibraryArray_mPath : [CanariLibraryEntry_mPath]
) -> StringArray {
//--- START OF USER ZONE 2
  var items = StringArray ()
//  if self_2E_usesSystemLibrary_0 {
//    items.append (fontLibraryPathForPath (systemLibraryPath ()))
//  }
  if self_usesUserLibrary {
    items.append (fontLibraryPathForPath (userLibraryPath ()))
  }
  var idx = 0
  while idx < self_additionnalLibraryArray_mUses.count {
    if self_additionnalLibraryArray_mUses [idx].mUses {
      items.append (fontLibraryPathForPath (self_additionnalLibraryArray_mPath [idx].mPath))
    }
    idx += 1
  }
  return items
//--- END OF USER ZONE 2
}

