extension Preferences {
  final func configure_configureFreeRoutingPath (_ inOutlet : AutoLayoutButton) {
//--- START OF USER ZONE 2
      inOutlet.title = FREEROUTING_DIR
      inOutlet.setClosureAction {
        let ws = NSWorkspace.shared
        let ok = ws.open (URL (fileURLWithPath: FREEROUTING_DIR))
        if !ok {
          let alert = NSAlert ()
          alert.messageText = "Cannot open the FreeRouting directory"
          alert.informativeText = "This directory does not exist."
          alert.beginSheetModal (for: inOutlet.window!) { (response : NSApplication.ModalResponse) in }
        }
      }
//--- END OF USER ZONE 2
  }
}

