  //····················································································································

  init (prefKey inPrefKey : String) {
    self.mPrefKey = inPrefKey
    super.init (usedForSignature: false, key: nil)
    if let array = UserDefaults.standard.array (forKey: inPrefKey) as? [[String : Any]] {
      var objectArray = EBReferenceArray <CanariLibraryEntry> ()
      for dictionary in array {
        let object = newInstanceOfEntityNamed (self.undoManager, "CanariLibraryEntry") as! CanariLibraryEntry
        object.setUpProperties (withDictionary: dictionary, managedObjectArray: [])
        objectArray.append (object)
      }
      self.setProp (objectArray)
    }
    toMany_mPath_StartsToBeObserved (by: self.mObserverForWritingPreferences)
    toMany_mUses_StartsToBeObserved (by: self.mObserverForWritingPreferences)
    toMany_mLibraryRepositoryURL_StartsToBeObserved (by: self.mObserverForWritingPreferences)
    toMany_mUserAndPasswordTag_StartsToBeObserved (by: self.mObserverForWritingPreferences)
    self.mObserverForWritingPreferences.mEventCallBack = { [weak self] in self?.writeInPreferences () }
 }

  //····················································································································

  private func writeInPreferences () {
    var dictionaryArray = [[String  : Any]] ()
    for object in self.mInternalArrayValue.values {
      var d = [String  : Any] ()
      object.saveIntoDictionary (&d)
      d [ENTITY_KEY] = nil // Remove entity key, not used in preferences
