    let size = str.size (withAttributes: textAttributes)
    let p = NSPoint (x: self.bounds.center.x - size.width / 2.0, y: self.bounds.center.y - size.height / 2.0)
    str.draw (at: p, withAttributes: textAttributes)
  }

