import AppKit

//--------------------------------------------------------------------------------------------------

extension AutoLayoutDeviceDocument {
  @objc func saveDocAction (_ inSender : NSObject?) {
//--- START OF USER ZONE 2
    let selectedDocArray = self.documentationController.selectedArray_property.propval
    if selectedDocArray.count == 1 {
      let selectedDoc = selectedDocArray [0]
      let savePanel = NSSavePanel ()
      savePanel.allowedFileTypes = ["pdf"]
      savePanel.allowsOtherFileTypes = false
      savePanel.nameFieldStringValue = selectedDoc.mFileName + ".pdf"
      savePanel.beginSheetModal (for: self.windowForSheet!) { inResponse in
        DispatchQueue.main.async {
          if inResponse == .OK, let url = savePanel.url {
            try? selectedDoc.mFileData.write (to: url)
          }
        }
      }
    }
//--- END OF USER ZONE 2
  }
}

