@MainActor func transient_SlavePadInDevice_padNumberDisplay (
       _ self_mCenterX : Int,                                
       _ self_mCenterY : Int,                                
       _ prefs_padNumberFont : NSFont,                       
       _ prefs_padNumberColor : NSColor,                     
       _ self_mMasterPad_mName : String?
) -> EBShape {
//--- START OF USER ZONE 2
      let textAttributes : [NSAttributedString.Key : Any] = [
        NSAttributedString.Key.font : prefs_padNumberFont,
        NSAttributedString.Key.foregroundColor : prefs_padNumberColor
      ]
      var shape = EBShape ()
      shape.add (text: 
        "(\(self_mMasterPad_mName ?? ""))",
        CanariPoint (x: self_mCenterX, y: self_mCenterY).cocoaPoint,
        textAttributes,
        .center,
        .center
      )
      return shape
//--- END OF USER ZONE 2
}

