func transient_FontRoot_issues (
       _ self_characters_issues : [FontCharacter_issues]
) -> CanariIssueArray {
//--- START OF USER ZONE 2
        var issues = [CanariIssue] ()
        for characterIssues in self_characters_issues {
          if let s = characterIssues.issues {
            issues += s
          }
        }
        return issues.sorted (by: { $0.representativeValue < $1.representativeValue } )
//--- END OF USER ZONE 2
}

