final class StandAloneArrayOf_FontRoot : ReadWriteArrayOf_FontRoot {

  //····················································································································

  override var selection : EBSelection < [FontRoot] > { return .single (self.mInternalArrayValue.values) }

  //····················································································································

  override func setProp (_ inValue : EBReferenceArray <FontRoot>) { self.mInternalArrayValue = inValue }

