@MainActor func transient_FontCharacter_gerberCodeInstructionCountMessage (
       _ self_gerberCode : CharacterGerberCode
) -> String {
//--- START OF USER ZONE 2
   let result : String
   let n = self_gerberCode.code.count
   if n == 0 {
     result = "No instruction"
   }else if n == 1 {
     result = "1 instruction"
   }else{
     result = "\(n) instructions"
   }
   return result
//--- END OF USER ZONE 2
}

