import Cocoa

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————

func transient_MergerDocument_incorrectDocumentFileErrorMessage (
       _ self_documentFilePath : String
) -> String {
//--- START OF USER ZONE 2
  var message = ""
  if self_documentFilePath == "" {
    message = "The document is unnamed."
  }else{
    var ok = true
    let baseName = self_documentFilePath.lastPathComponent.deletingPathExtension
    for char in baseName.unicodeScalars {
      ok = (char >= "A") && (char <= "Z")
      if !ok {
        ok = (char >= "a") && (char <= "z")
      }
      if !ok {
        ok = (char >= "0") && (char <= "9")
      }
      if !ok {
        ok = (char == "-") || (char == "_")
      }
      if !ok {
        break
      }
    }
    if !ok {
      message  = "The name of the document (\(baseName)) contains characters that can cause problems for PCB"
      message += " manufacturers. It is recommended that this name only contains letters (without any accent),"
      message += " digits, and the characters '_', '-'."
    }
  }
  return message
//--- END OF USER ZONE 2
}

