@MainActor func transient_AutoLayoutPackageDocument_addBezierButtonImage (
       _ prefs_packageColor : NSColor
) -> NSImage {
//--- START OF USER ZONE 2
        let temporaryObject = PackageBezier (nil)
        if let displayShape = temporaryObject.objectDisplay {
          let r = displayShape.boundingBox
          if !r.isEmpty {
            return buildPDFimage (frame: r.insetBy(dx: -2.0, dy: -2.0), shape: displayShape)
          }else{
            return NSImage (named: NSImage.statusPartiallyAvailableName)!
          }
        }else{
          return NSImage (named: NSImage.statusPartiallyAvailableName)!
        }
//--- END OF USER ZONE 2
}

