@MainActor func transient_AutoLayoutPackageDocument_addMasterPadButtonImage (
       _ prefs_frontSidePadColor : NSColor
) -> NSImage {
//--- START OF USER ZONE 2
        let temporaryObject = PackagePad (nil)
        if let displayShape = temporaryObject.objectDisplay {
          let r = displayShape.boundingBox
          if !r.isEmpty {
            return buildPDFimage (frame: r, shape: displayShape)
          }else{
            return NSImage (named: NSImage.statusPartiallyAvailableName)!
          }
        }else{
          return NSImage (named: NSImage.statusPartiallyAvailableName)!
        }
//--- END OF USER ZONE 2
}

