@MainActor func transient_AutoLayoutPackageDocument_addZoneButtonImage (
       _ prefs_padZoneColor : NSColor
) -> NSImage {
//--- START OF USER ZONE 2
        var bp = BezierPath (rect: NSRect (x: 0, y: 0, width: 16, height: 16))
