func transient_PackageDimension_objectDisplay (
       _ self_x1 : Int,                        
       _ self_y1 : Int,                        
       _ self_x2 : Int,                        
       _ self_y2 : Int,                        
       _ self_xDimension : Int,                
       _ self_yDimension : Int,                
       _ self_distanceInCanariUnit : Int,      
       _ self_distanceUnit : Int,              
       _ prefs_dimensionFont : NSFont,         
       _ prefs_packageBackgroundColor : NSColor,
       _ prefs_packageDimensionColor : NSColor
) -> EBShape {
//--- START OF USER ZONE 2
  let arrowSize : CGFloat = 0.5
  let p1 = NSPoint (x: canariUnitToCocoa (self_x1), y: canariUnitToCocoa (self_y1))
  let p2 = NSPoint (x: canariUnitToCocoa (self_x2), y: canariUnitToCocoa (self_y2))
  let length = NSPoint.distance (p1, p2)
  var shape = EBShape ()
 //--- Compute angle
  let angle = NSPoint.angleInRadian (p1, p2)
 //--- Draw line
  var bp = EBBezierPath ()
  if length <= (4.0 * arrowSize) {
    bp.move (to: p1)
    bp.line (to: p2)
    bp.lineCapStyle = .butt
  }else{
  //--- Segment
    let center = NSPoint.center (p1, p2)
    var tr = AffineTransform ()
    tr.translate (x: center.x, y: center.y)
    tr.rotate (byRadians: angle)
    let lg = length / 2.0 - arrowSize
    bp.move (to: NSPoint (x: -lg, y: 0.0))
    bp.line (to: NSPoint (x:  lg, y: 0.0))
    bp.transform(using: tr)
    bp.lineCapStyle = .butt
  }
  bp.lineCapStyle = .round
  bp.lineWidth = 0.05
  shape.add (stroke: [bp], NSColor.orange)
  let rotationIfSmall : CGFloat = (length <= (4.0 * arrowSize)) ? .pi : 0.0 ;
//------- Arrow at first point
  var tr = AffineTransform ()
  tr.translate (x: p1.x, y: p1.y)
  tr.rotate (byRadians: angle + rotationIfSmall)
  var path1 = EBBezierPath ()
  path1.move (to: NSPoint (x: 0.0, y: 0.0))
  path1.line (to: NSPoint (x: 2.0 * arrowSize, y:  arrowSize))
  path1.curve (to: NSPoint (x: 2.0 * arrowSize, y: -arrowSize),
               controlPoint1: NSPoint (x: arrowSize, y: 0.0),
               controlPoint2: NSPoint (x: arrowSize, y: 0.0))
  path1.close ()
  path1.transform (using: tr)
//------- Arrow at second point
  tr = AffineTransform ()
  tr.translate (x: p2.x, y: p2.y)
  tr.rotate (byRadians: angle + .pi + rotationIfSmall)
  var path2 = EBBezierPath ()
  path2.move (to: NSPoint (x: 0.0, y: 0.0))
  path2.line (to:NSPoint (x: 2.0 * arrowSize, y:  arrowSize))
  path2.curve (to:NSPoint (x: 2.0 * arrowSize, y: -arrowSize),
               controlPoint1: NSPoint (x: arrowSize, y: 0.0),
               controlPoint2: NSPoint (x: arrowSize, y: 0.0))
  path2.close ()
  path2.transform (using: tr)
  shape.add (filled: [path1, path2], prefs_packageDimensionColor)
//------- Add dimension text
  let dimensionText = stringFrom (valueInCanariUnit: self_distanceInCanariUnit, displayUnit: self_distanceUnit)
  let p = CanariPoint (x: self_xDimension + (self_x1 + self_x2) / 2, y: self_yDimension + (self_y1 + self_y2) / 2).cocoaPoint
  let textAttributes : [NSAttributedString.Key : Any] = [
    NSAttributedString.Key.font : prefs_dimensionFont,
    NSAttributedString.Key.foregroundColor : prefs_packageDimensionColor,
    NSAttributedString.Key.backgroundColor : prefs_packageBackgroundColor
  ]
  shape.add (text: dimensionText, p, textAttributes, .center, .center)
//---
  return shape
//--- END OF USER ZONE 2
}

