@MainActor func transient_PackageGuide_objectDisplay (
       _ self_x1 : Int,                    
       _ self_y1 : Int,                    
       _ self_x2 : Int,                    
       _ self_y2 : Int
) -> EBShape {
//--- START OF USER ZONE 2
  var bp = EBBezierPath ()
  bp.move (to: NSPoint (x: canariUnitToCocoa (self_x1), y: canariUnitToCocoa (self_y1)))
