@MainActor func transient_PackageSegment_lengthInCanariUnit (
       _ self_x1 : Int,                           
       _ self_y1 : Int,                           
       _ self_x2 : Int,                           
       _ self_y2 : Int
) -> Int {
//--- START OF USER ZONE 2
    let dx = CGFloat (self_x1 - self_x2)
    let dy = CGFloat (self_y1 - self_y2)
    let length = sqrt (dx * dx + dy * dy).rounded (.toNearestOrEven)
    return Int (length)
//--- END OF USER ZONE 2
}

