import AppKit

//--------------------------------------------------------------------------------------------------

extension AutoLayoutProjectDocument {
  @objc func revealComponentsInBoardAction (_ inSender : NSObject?) {
//--- START OF USER ZONE 2
    var componentsToSelect = EBReferenceSet <ComponentInProject> ()
    for element in self.schematicObjectsController.selectedSet.values {
      if let symbol = element as? ComponentSymbolInProject,
         let component = symbol.mComponent,
         let isInBoard = component.isPlacedInBoard, isInBoard {
        componentsToSelect.insert (component)
      }
    }
    if componentsToSelect.isEmpty {
      NSSound.beep ()
    }else{
      self.rootObject.mSelectedPageIndex_property.setProp (6) // Page « Board »
      self.boardObjectsController.addToSelection (objects: Array (componentsToSelect.values))
    }
//--- END OF USER ZONE 2
  }
}

