import AppKit

//--------------------------------------------------------------------------------------------------

extension AutoLayoutProjectDocument {
  @objc func setBoardTextFontAction (_ inSender : NSObject?) {
//--- START OF USER ZONE 2
        if let popUpButton = inSender as? NSPopUpButton, let fontName = popUpButton.selectedItem?.title {
          var boardFont : FontInProject? = nil
          for font in self.rootObject.mFonts.values {
            if font.mFontName == fontName {
              boardFont = font
            }
          }
          for selectedText in self.boardTextSelectionController.selectedArray.values {
            selectedText.mFont = boardFont
          }
        }
//--- END OF USER ZONE 2
  }
}

