@MainActor func transient_BoardRestrictRectangle_selectionDisplay (
       _ self_mX : Int,                                            
       _ self_mY : Int,                                            
       _ self_mWidth : Int,                                        
       _ self_mHeight : Int,                                       
       _ prefs_hiliteWidthMultipliedByTen : Int
) -> EBShape {
//--- START OF USER ZONE 2
        let x = canariUnitToCocoa (self_mX)
        let y = canariUnitToCocoa (self_mY)
        let width = canariUnitToCocoa (self_mWidth)
        let height = canariUnitToCocoa (self_mHeight)
        let r = NSRect (x: x, y: y, width: width, height: height)
        var bp = EBBezierPath (rect: r)
        bp.lineWidth = CGFloat (prefs_hiliteWidthMultipliedByTen) / 10.0
        bp.lineCapStyle = .round
        bp.lineJoinStyle = .round
        var shape = EBShape ()
        shape.add (stroke: [bp], .cyan)
      //--- Knobs
        shape.add (knobAt:  NSPoint (x: x + width / 2.0, y: y), knobIndex: BOARD_RESTRICT_RECT_BOTTOM, .diamond, 2.0)
        shape.add (knobAt:  NSPoint (x: x + width / 2.0, y: y + height), knobIndex: BOARD_RESTRICT_RECT_TOP, .diamond, 2.0)
        shape.add (knobAt:  NSPoint (x: x + width, y: y + height / 2.0), knobIndex: BOARD_RESTRICT_RECT_RIGHT, .diamond, 2.0)
        shape.add (knobAt:  NSPoint (x: x, y: y + height / 2.0), knobIndex: BOARD_RESTRICT_RECT_LEFT, .diamond, 2.0)
      //---
        return shape
//--- END OF USER ZONE 2
}

