@MainActor func transient_BoardText_objectDisplay (
       _ self_mX : Int,                            
       _ self_mY : Int,                            
       _ self_mText : String,                      
       _ self_mFontSize : Double,                  
       _ self_mFont_descriptor : BoardFontDescriptor?,
       _ self_mHorizontalAlignment : HorizontalAlignment,
       _ self_mVerticalAlignment : BoardTextVerticalAlignment,
       _ self_mLayer : BoardTextLayer,             
       _ self_mRotation : Int,                     
       _ self_mWeight : Double,                    
       _ self_mOblique : Bool,                     
       _ self_BoardObject_displayFrontLegendForBoard : Bool,
       _ self_BoardObject_displayBackLegendForBoard : Bool,
       _ self_BoardObject_displayFrontLayoutForBoard : Bool,
       _ self_BoardObject_displayBackLayoutForBoard : Bool,
       _ prefs_frontSideLegendColorForBoard : NSColor,
       _ prefs_frontSideLayoutColorForBoard : NSColor,
       _ prefs_backSideLayoutColorForBoard : NSColor,
       _ prefs_backSideLegendColorForBoard : NSColor
) -> EBShape {
//--- START OF USER ZONE 2
        let (textBP, _, _, _, _) = boardText_displayInfos (
          x: self_mX,
          y: self_mY,
          string: self_mText,
          fontSize: self_mFontSize,
          self_mFont_descriptor!,
          horizontalAlignment: self_mHorizontalAlignment,
          verticalAlignment: self_mVerticalAlignment,
          frontSide: (self_mLayer == .layoutFront) || (self_mLayer == .legendFront),
          rotation: self_mRotation,
          weight: self_mWeight,
          oblique: self_mOblique,
          extraWidth: 0.0
        )
        let textColor : NSColor
        let display : Bool
        switch self_mLayer {
        case .legendFront :
          textColor = prefs_frontSideLegendColorForBoard
          display = self_BoardObject_displayFrontLegendForBoard
        case .layoutFront :
          textColor = prefs_frontSideLayoutColorForBoard
          display = self_BoardObject_displayFrontLayoutForBoard
        case .layoutBack :
          textColor = prefs_backSideLayoutColorForBoard
          display = self_BoardObject_displayBackLayoutForBoard
        case .legendBack :
          textColor = prefs_backSideLegendColorForBoard
          display = self_BoardObject_displayBackLegendForBoard
        }
        var shape = EBShape ()
        if display {
          let textShape = EBShape (stroke: [textBP], textColor)
          let backgroundBP = EBBezierPath (rect: textShape.boundingBox) //--- Transparent background
          shape.add (filled: [backgroundBP], nil)
          shape.add (textShape)
        }
      //---
        return shape
//--- END OF USER ZONE 2
}

