@MainActor func transient_NonPlatedHole_selectionDisplay (
       _ prefs_selectionHiliteColor : NSColor,            
       _ self_mX : Int,                                   
       _ self_mY : Int,                                   
       _ self_mWidth : Int,                               
       _ self_mHeight : Int,                              
       _ prefs_hiliteWidthMultipliedByTen : Int
) -> EBShape {
//--- START OF USER ZONE 2
       var shape = EBShape ()
//       let color = NSColor.black
       let r = CanariRect (
         center: CanariPoint (x: self_mX, y: self_mY),
         size: CanariSize (width: self_mWidth + prefs_hiliteWidthMultipliedByTen / 5, height: self_mHeight + prefs_hiliteWidthMultipliedByTen / 5)
       )
       let bp = EBBezierPath (oblongInRect: r.cocoaRect)
       shape.add (filled: [bp], prefs_selectionHiliteColor)
       return shape
//--- END OF USER ZONE 2
}

