func transient_ProjectRoot_boardStatusMessage (
       _ self_boardIssues : CanariIssueArray
) -> String {
//--- START OF USER ZONE 2
        var statusMessage = [String] ()
        var errorCount = 0
        var warningCount = 0
        for issue in self_boardIssues {
          switch issue.kind {
          case .error :
            errorCount += 1
            statusMessage.append ("Error: \(issue.message)")
          case .warning :
            warningCount += 1
            statusMessage.append ("Warning: \(issue.message)")
          }
        }
        var a = [String] ()
        if errorCount == 1 {
          a.append ("1 error.")
        }else if errorCount > 1 {
          a.append ("\(errorCount) errors.")
        }
        if warningCount == 1 {
          a.append ("1 warning.")
        }else if warningCount > 1 {
          a.append ("\(warningCount) warnings.")
        }
        if a.isEmpty {
          a.append ("Ok.")
        }
        return (a + statusMessage).joined (separator: "\n")
//--- END OF USER ZONE 2
}

