func transient_ProjectRoot_componentsPlacedInBoard (
       _ self_mComponents_mNamePrefix : [ComponentInProject_mNamePrefix],
       _ self_mComponents_mNameIndex : [ComponentInProject_mNameIndex],
       _ self_mComponents_componentIsPlacedInBoard : [ComponentInProject_componentIsPlacedInBoard]
) -> StringTagArray {
//--- START OF USER ZONE 2
        var result = [StringTag] ()
        for idx in 0 ..< self_mComponents_componentIsPlacedInBoard.count {
          if let isPlacedInBoard = self_mComponents_componentIsPlacedInBoard [idx].componentIsPlacedInBoard,
             isPlacedInBoard {
            let prefix = self_mComponents_mNamePrefix [idx].mNamePrefix
            let index = self_mComponents_mNameIndex [idx].mNameIndex
            result.append (StringTag (string: prefix, tag:index))
          }
        }
        return result
//--- END OF USER ZONE 2
}

