@MainActor func transient_SheetInProject_connectedPoints (
       _ prefs_connectionColorForSchematic : NSColor,     
       _ self_mPoints_connectedPoints : [PointInSchematic_connectedPoints]
) -> EBShape {
//--- START OF USER ZONE 2
        var bp = EBBezierPath ()
        for object in self_mPoints_connectedPoints {
          if let a = object.connectedPoints {
            for point in a {
              let p = point.cocoaPoint
              let r = NSRect (
                x: p.x - SCHEMATIC_CONNECTION_POINT_DIAMETER / 2.0,
                y: p.y - SCHEMATIC_CONNECTION_POINT_DIAMETER / 2.0,
                width: SCHEMATIC_CONNECTION_POINT_DIAMETER,
                height: SCHEMATIC_CONNECTION_POINT_DIAMETER
              )
              bp.appendOval (in: r)
            }
          }
        }
        return EBShape (filled: [bp], prefs_connectionColorForSchematic)
//--- END OF USER ZONE 2
}

