func transient_NetClassInProject_netWarningCount (
       _ self_netsDescription : NetInfoArray
) -> Int {
//--- START OF USER ZONE 2
        var warningCount = 0
        for netInfo in self_netsDescription {
          if (netInfo.pinCount < 2) || netInfo.subnetsHaveWarning {
            warningCount += 1
          }
        }
        return warningCount
