  //····················································································································

  private func sortedIndexArrayOfSelectedObjects () -> [Int] {
    var result = [Int] ()
    let objects = self.objectArray
    for object in self.selectedArray_property.propset {
      let idx = objects.firstIndex (of: object)!
      result.append (idx)
    }
    return result.sorted ()
  }

