@MainActor func transient_AutoLayoutSymbolDocument_segmentedControlSegmentIssueImage (
       _ self_symbolIssues : CanariIssueArray
) -> NSImage {
//--- START OF USER ZONE 2
    var errorCount = 0
    var warningCount = 0
    for issue in self_symbolIssues {
      switch issue.kind {
      case .error :
        errorCount += 1
      case .warning :
        warningCount += 1
      }
    }
    var image = NSImage (named: okStatusImageName)!
    if errorCount > 0 {
      image = NSImage (named: errorStatusImageName)!
    }else if warningCount > 0 {
      image = NSImage (named: warningStatusImageName)!
    }
    return image//--- END OF USER ZONE 2
}

