@MainActor func transient_SymbolBezierCurve_selectionDisplay (
       _ self_x1 : Int,                                       
       _ self_y1 : Int,                                       
       _ self_x2 : Int,                                       
       _ self_y2 : Int,                                       
       _ self_cpx1 : Int,                                     
       _ self_cpy1 : Int,                                     
       _ self_cpx2 : Int,                                     
       _ self_cpy2 : Int
) -> EBShape {
//--- START OF USER ZONE 2
  let p1  = NSPoint (x: canariUnitToCocoa (self_x1),   y: canariUnitToCocoa (self_y1))
  let p2  = NSPoint (x: canariUnitToCocoa (self_x2),   y: canariUnitToCocoa (self_y2))
  let cp1 = NSPoint (x: canariUnitToCocoa (self_cpx1), y: canariUnitToCocoa (self_cpy1))
  let cp2 = NSPoint (x: canariUnitToCocoa (self_cpx2), y: canariUnitToCocoa (self_cpy2))
  var bp = EBBezierPath ()
  bp.move (to: p1)
  bp.curve (to: p2, controlPoint1: cp1, controlPoint2: cp2)
  bp.move (to: p1)
  bp.line (to: cp1)
  bp.move (to: p2)
  bp.line (to: cp2)
  bp.lineWidth = 0.25
  bp.lineCapStyle = .round
  var shape = EBShape ()
  shape.add (stroke: [bp], NSColor.cyan)
  shape.add (knobAt:  p1, knobIndex: SYMBOL_BEZIER_CURVE_ENDPOINT_1, .diamond, 2.0)
  shape.add (knobAt:  p2, knobIndex: SYMBOL_BEZIER_CURVE_ENDPOINT_2, .diamond, 2.0)
  shape.add (knobAt:  cp1, knobIndex: SYMBOL_BEZIER_CURVE_CONTROL_1, .diamond, 2.0)
  shape.add (knobAt:  cp2, knobIndex: SYMBOL_BEZIER_CURVE_CONTROL_2, .diamond, 2.0)
  return shape
//--- END OF USER ZONE 2
}

