@MainActor func transient_SymbolSegment_selectionDisplay (
       _ prefs_selectionHiliteColor : NSColor,            
       _ self_x1 : Int,                                   
       _ self_y1 : Int,                                   
       _ self_x2 : Int,                                   
       _ self_y2 : Int
) -> EBShape {
//--- START OF USER ZONE 2
  let p1 = NSPoint (x: canariUnitToCocoa (self_x1), y: canariUnitToCocoa (self_y1))
  let p2 = NSPoint (x: canariUnitToCocoa (self_x2), y: canariUnitToCocoa (self_y2))
  var bp = EBBezierPath ()
  bp.move (to: p1)
  bp.line (to: p2)
  bp.lineWidth = 0.25
  bp.lineCapStyle = .round
  var shape = EBShape ()
  shape.add (stroke: [bp], prefs_selectionHiliteColor)
  shape.add (knobAt:  p1, knobIndex: SYMBOL_SEGMENT_ENDPOINT_1, .diamond, 2.0)
  shape.add (knobAt:  p2, knobIndex: SYMBOL_SEGMENT_ENDPOINT_2, .diamond, 2.0)
  return shape
//--- END OF USER ZONE 2
}

