@MainActor func transient_SymbolSolidOval_objectDisplay (
       _ self_x : Int,                                   
       _ self_y : Int,                                   
       _ self_width : Int,                               
       _ self_height : Int,                              
       _ prefs_symbolColor : NSColor
) -> EBShape {
//--- START OF USER ZONE 2
  let r = NSRect (
    x: canariUnitToCocoa (self_x),
    y: canariUnitToCocoa (self_y),
    width: canariUnitToCocoa (self_width),
    height: canariUnitToCocoa (self_height)
  )
  let bp = EBBezierPath (ovalIn: r)
  var shape = EBShape ()
  shape.add (filled: [bp], prefs_symbolColor)
  return shape
//--- END OF USER ZONE 2
}

