  switch inDocumentData.documentFileFormat {
  case .textual :
    return try dataForTextualSaveOperation (from: inDocumentData)
  case .binary :
    return try dataForBinarySaveOperation (from: inDocumentData)
  }
}

//——————————————————————————————————————————————————————————————————————————————————————————————————————————————————————
//   save
